import React from 'react';

class Counter extends React.Component {
    state = {
        count: 0
    }

    increment() {
        this.setState({ count: this.state.count++ })
    } 


    render() {
        return (
            <div>
                <button onClick={this.increment()}>-</button>
                <span>{this.state.count}</span>
                <button>+</button>
            </div>
        )
    }
}

export default Counter;
