import React from 'react';

class CounterButton extends React.Component {
    state = {
        count: 0
    }

    increment() {
        this.setState({ count: this.state.count + 1 })
    }

    decrement() {
        this.setState({ count: this.state.count - 1 })
    }


    render() {
        return <button onClick={() => this.decrement()}>-</button>
    }
}

export default CounterButton;
