import React from 'react';

class InputText extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            tasks: ''
        }
      }

    getTasks = () => {
        fetch('https://jsonplaceholder.typicode.com/todos')
            .then(response => response.json())
            .then(tasks => {
                this.setState({ tasks: tasks.slice(0, 5) });
            });         

    }

    render() {
        return (
            <div>
                <input type="text" /><br />
                {this.state.text}
            </div>
        )

    }
}

export default InputText;
