import React from 'react';

class SelectText extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            text: ''
        }
      }

      changeSelect = (e) => {
        e.preventDefault();
        this.setState({ text: e.currentTarget.value})      

    }

    render() {
        return (
            <div>
                <select onChange={this.changeSelect}/> => {this.state.text}
            </div>
        )

    }
}

export default SelectText;
